    <script src="https://cdnjs.cloudflare.com/ajax/libs/iframe-resizer/4.1.1/iframeResizer.min.js" defer></script>
    <script>
        $(document).ready(function() {
            $('.menu-toggle').on('click', function() {
                $(this).toggleClass('active');
                $('.mobile-menu').slideToggle(300);
            });


            $('.mobile-menu .has-submenu').on('click', function(e) {
                e.preventDefault();
                $(this).next('.mobile-submenu').slideToggle(300);
                $(this).toggleClass('active');
            });

            // Close mobile menu on window resize
            $(window).on('resize', function() {
                if ($(window).width() > 991) {
                    $('.mobile-menu').slideUp(300);
                    $('.menu-toggle').removeClass('active');
                }
            });

            // Close menu when clicking outside
            $(document).on('click', function(e) {
                if (!$(e.target).closest('.mobile-menu, .menu-toggle').length) {
                    $('.mobile-menu').slideUp(300);
                    $('.menu-toggle').removeClass('active');
                }
            });
        });
    </script>
