        </div>
    </div>

    <x-dynamic-component :component="inspirecms_templates()->getComponentWithTheme('footer')" :locale="$locale" />

    <!-- Scripts -->
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/swiper@8/swiper-bundle.min.js"></script>
    <script>
        const toggleButton = document.querySelector('.sidebar .mobile-top-nav .mobile-menu-btn');
        const sidebar = document.querySelector('.sidebar .mobile-menu-wrapper');
        const sidebarCloseButton = document.querySelector('.sidebar .mobile-menu-wrapper .close-menu');

        toggleButton.addEventListener('click', () => {
            sidebar.classList.toggle('active');
        });
        sidebarCloseButton.addEventListener('click', () => {
            sidebar.classList.remove('active');
        });
    </script>
</body>

</html>
